<script>
export default {
  data() {
    return {
      imgUrl: ""
    };
  },
  methods: {
    logoClick() {
      if(this.$route.path != "/home") this.$router.push('/home');
    },
    avatarClick() {
      if(this.$route.path != "/profile") this.$router.push('/profile');
    },
    async getImgUrl() {
      if(sessionStorage.getItem("id")) {
        const res = await this.$http.get("/user/" + sessionStorage.getItem("id"));
        this.imgUrl = res[0].user_img;
      }
    }
  },
  watch: {
    $route() {
      this.getImgUrl();
    }
  },
  mounted() {
    this.getImgUrl();
  }
};
</script>

